#include "CurrentStateObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::taraxa {
namespace object {

CurrentState::CurrentState(std::unique_ptr<const Concept>&& pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames CurrentState::getTypeNames() const noexcept
{
	return {
		R"gql(CurrentState)gql"sv
	};
}

service::ResolverMap CurrentState::getResolvers() const noexcept
{
	return {
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(finalBlock)gql"sv, [this](service::ResolverParams&& params) { return resolveFinalBlock(std::move(params)); } },
		{ R"gql(dagBlockLevel)gql"sv, [this](service::ResolverParams&& params) { return resolveDagBlockLevel(std::move(params)); } },
		{ R"gql(dagBlockPeriod)gql"sv, [this](service::ResolverParams&& params) { return resolveDagBlockPeriod(std::move(params)); } }
	};
}

void CurrentState::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void CurrentState::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver CurrentState::resolveFinalBlock(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getFinalBlock(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver CurrentState::resolveDagBlockLevel(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getDagBlockLevel(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver CurrentState::resolveDagBlockPeriod(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getDagBlockPeriod(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver CurrentState::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(CurrentState)gql" }, std::move(params));
}

} // namespace object

void AddCurrentStateDetails(const std::shared_ptr<schema::ObjectType>& typeCurrentState, const std::shared_ptr<schema::Schema>& schema)
{
	typeCurrentState->AddFields({
		schema::Field::Make(R"gql(finalBlock)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv))),
		schema::Field::Make(R"gql(dagBlockLevel)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv))),
		schema::Field::Make(R"gql(dagBlockPeriod)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv)))
	});
}

} // namespace graphql::taraxa
