#pragma once

#ifndef LOGOBJECT_H
#define LOGOBJECT_H

#include "TaraxaSchema.h"

namespace graphql::taraxa::object {
namespace methods::LogHas {

template <class TImpl>
concept getIndexWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableScalar<int> { impl.getIndex(std::move(params)) } };
};

template <class TImpl>
concept getIndex = requires (TImpl impl)
{
	{ service::AwaitableScalar<int> { impl.getIndex() } };
};

template <class TImpl>
concept getAccountWithParams = requires (TImpl impl, service::FieldParams params, std::optional<response::Value> blockArg)
{
	{ service::AwaitableObject<std::shared_ptr<Account>> { impl.getAccount(std::move(params), std::move(blockArg)) } };
};

template <class TImpl>
concept getAccount = requires (TImpl impl, std::optional<response::Value> blockArg)
{
	{ service::AwaitableObject<std::shared_ptr<Account>> { impl.getAccount(std::move(blockArg)) } };
};

template <class TImpl>
concept getTopicsWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableScalar<std::vector<response::Value>> { impl.getTopics(std::move(params)) } };
};

template <class TImpl>
concept getTopics = requires (TImpl impl)
{
	{ service::AwaitableScalar<std::vector<response::Value>> { impl.getTopics() } };
};

template <class TImpl>
concept getDataWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableScalar<response::Value> { impl.getData(std::move(params)) } };
};

template <class TImpl>
concept getData = requires (TImpl impl)
{
	{ service::AwaitableScalar<response::Value> { impl.getData() } };
};

template <class TImpl>
concept getTransactionWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<Transaction>> { impl.getTransaction(std::move(params)) } };
};

template <class TImpl>
concept getTransaction = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<Transaction>> { impl.getTransaction() } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

} // namespace methods::LogHas

class [[nodiscard]] Log final
	: public service::Object
{
private:
	[[nodiscard]] service::AwaitableResolver resolveIndex(service::ResolverParams&& params) const;
	[[nodiscard]] service::AwaitableResolver resolveAccount(service::ResolverParams&& params) const;
	[[nodiscard]] service::AwaitableResolver resolveTopics(service::ResolverParams&& params) const;
	[[nodiscard]] service::AwaitableResolver resolveData(service::ResolverParams&& params) const;
	[[nodiscard]] service::AwaitableResolver resolveTransaction(service::ResolverParams&& params) const;

	[[nodiscard]] service::AwaitableResolver resolve_typename(service::ResolverParams&& params) const;

	struct [[nodiscard]] Concept
	{
		virtual ~Concept() = default;

		virtual void beginSelectionSet(const service::SelectionSetParams& params) const = 0;
		virtual void endSelectionSet(const service::SelectionSetParams& params) const = 0;

		[[nodiscard]] virtual service::AwaitableScalar<int> getIndex(service::FieldParams&& params) const = 0;
		[[nodiscard]] virtual service::AwaitableObject<std::shared_ptr<Account>> getAccount(service::FieldParams&& params, std::optional<response::Value>&& blockArg) const = 0;
		[[nodiscard]] virtual service::AwaitableScalar<std::vector<response::Value>> getTopics(service::FieldParams&& params) const = 0;
		[[nodiscard]] virtual service::AwaitableScalar<response::Value> getData(service::FieldParams&& params) const = 0;
		[[nodiscard]] virtual service::AwaitableObject<std::shared_ptr<Transaction>> getTransaction(service::FieldParams&& params) const = 0;
	};

	template <class T>
	struct [[nodiscard]] Model
		: Concept
	{
		Model(std::shared_ptr<T>&& pimpl) noexcept
			: _pimpl { std::move(pimpl) }
		{
		}

		[[nodiscard]] service::AwaitableScalar<int> getIndex(service::FieldParams&& params) const final
		{
			if constexpr (methods::LogHas::getIndexWithParams<T>)
			{
				return { _pimpl->getIndex(std::move(params)) };
			}
			else
			{
				static_assert(methods::LogHas::getIndex<T>, R"msg(Log::getIndex is not implemented)msg");
				return { _pimpl->getIndex() };
			}
		}

		[[nodiscard]] service::AwaitableObject<std::shared_ptr<Account>> getAccount(service::FieldParams&& params, std::optional<response::Value>&& blockArg) const final
		{
			if constexpr (methods::LogHas::getAccountWithParams<T>)
			{
				return { _pimpl->getAccount(std::move(params), std::move(blockArg)) };
			}
			else
			{
				static_assert(methods::LogHas::getAccount<T>, R"msg(Log::getAccount is not implemented)msg");
				return { _pimpl->getAccount(std::move(blockArg)) };
			}
		}

		[[nodiscard]] service::AwaitableScalar<std::vector<response::Value>> getTopics(service::FieldParams&& params) const final
		{
			if constexpr (methods::LogHas::getTopicsWithParams<T>)
			{
				return { _pimpl->getTopics(std::move(params)) };
			}
			else
			{
				static_assert(methods::LogHas::getTopics<T>, R"msg(Log::getTopics is not implemented)msg");
				return { _pimpl->getTopics() };
			}
		}

		[[nodiscard]] service::AwaitableScalar<response::Value> getData(service::FieldParams&& params) const final
		{
			if constexpr (methods::LogHas::getDataWithParams<T>)
			{
				return { _pimpl->getData(std::move(params)) };
			}
			else
			{
				static_assert(methods::LogHas::getData<T>, R"msg(Log::getData is not implemented)msg");
				return { _pimpl->getData() };
			}
		}

		[[nodiscard]] service::AwaitableObject<std::shared_ptr<Transaction>> getTransaction(service::FieldParams&& params) const final
		{
			if constexpr (methods::LogHas::getTransactionWithParams<T>)
			{
				return { _pimpl->getTransaction(std::move(params)) };
			}
			else
			{
				static_assert(methods::LogHas::getTransaction<T>, R"msg(Log::getTransaction is not implemented)msg");
				return { _pimpl->getTransaction() };
			}
		}

		void beginSelectionSet(const service::SelectionSetParams& params) const final
		{
			if constexpr (methods::LogHas::beginSelectionSet<T>)
			{
				_pimpl->beginSelectionSet(params);
			}
		}

		void endSelectionSet(const service::SelectionSetParams& params) const final
		{
			if constexpr (methods::LogHas::endSelectionSet<T>)
			{
				_pimpl->endSelectionSet(params);
			}
		}

	private:
		const std::shared_ptr<T> _pimpl;
	};

	Log(std::unique_ptr<const Concept>&& pimpl) noexcept;

	[[nodiscard]] service::TypeNames getTypeNames() const noexcept;
	[[nodiscard]] service::ResolverMap getResolvers() const noexcept;

	void beginSelectionSet(const service::SelectionSetParams& params) const final;
	void endSelectionSet(const service::SelectionSetParams& params) const final;

	const std::unique_ptr<const Concept> _pimpl;

public:
	template <class T>
	Log(std::shared_ptr<T> pimpl) noexcept
		: Log { std::unique_ptr<const Concept> { std::make_unique<Model<T>>(std::move(pimpl)) } }
	{
	}

	[[nodiscard]] static constexpr std::string_view getObjectType() noexcept
	{
		return { R"gql(Log)gql" };
	}
};

} // namespace graphql::taraxa::object

#endif // LOGOBJECT_H
