#pragma once

#ifndef MUTATIONOBJECT_H
#define MUTATIONOBJECT_H

#include "TaraxaSchema.h"

namespace graphql::taraxa::object {
namespace methods::MutationHas {

template <class TImpl>
concept applySendRawTransactionWithParams = requires (TImpl impl, service::FieldParams params, response::Value dataArg)
{
	{ service::AwaitableScalar<response::Value> { impl.applySendRawTransaction(std::move(params), std::move(dataArg)) } };
};

template <class TImpl>
concept applySendRawTransaction = requires (TImpl impl, response::Value dataArg)
{
	{ service::AwaitableScalar<response::Value> { impl.applySendRawTransaction(std::move(dataArg)) } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

} // namespace methods::MutationHas

class [[nodiscard]] Mutation final
	: public service::Object
{
private:
	[[nodiscard]] service::AwaitableResolver resolveSendRawTransaction(service::ResolverParams&& params) const;

	[[nodiscard]] service::AwaitableResolver resolve_typename(service::ResolverParams&& params) const;

	struct [[nodiscard]] Concept
	{
		virtual ~Concept() = default;

		virtual void beginSelectionSet(const service::SelectionSetParams& params) const = 0;
		virtual void endSelectionSet(const service::SelectionSetParams& params) const = 0;

		[[nodiscard]] virtual service::AwaitableScalar<response::Value> applySendRawTransaction(service::FieldParams&& params, response::Value&& dataArg) const = 0;
	};

	template <class T>
	struct [[nodiscard]] Model
		: Concept
	{
		Model(std::shared_ptr<T>&& pimpl) noexcept
			: _pimpl { std::move(pimpl) }
		{
		}

		[[nodiscard]] service::AwaitableScalar<response::Value> applySendRawTransaction(service::FieldParams&& params, response::Value&& dataArg) const final
		{
			if constexpr (methods::MutationHas::applySendRawTransactionWithParams<T>)
			{
				return { _pimpl->applySendRawTransaction(std::move(params), std::move(dataArg)) };
			}
			else
			{
				static_assert(methods::MutationHas::applySendRawTransaction<T>, R"msg(Mutation::applySendRawTransaction is not implemented)msg");
				return { _pimpl->applySendRawTransaction(std::move(dataArg)) };
			}
		}

		void beginSelectionSet(const service::SelectionSetParams& params) const final
		{
			if constexpr (methods::MutationHas::beginSelectionSet<T>)
			{
				_pimpl->beginSelectionSet(params);
			}
		}

		void endSelectionSet(const service::SelectionSetParams& params) const final
		{
			if constexpr (methods::MutationHas::endSelectionSet<T>)
			{
				_pimpl->endSelectionSet(params);
			}
		}

	private:
		const std::shared_ptr<T> _pimpl;
	};

	Mutation(std::unique_ptr<const Concept>&& pimpl) noexcept;

	[[nodiscard]] service::TypeNames getTypeNames() const noexcept;
	[[nodiscard]] service::ResolverMap getResolvers() const noexcept;

	void beginSelectionSet(const service::SelectionSetParams& params) const final;
	void endSelectionSet(const service::SelectionSetParams& params) const final;

	const std::unique_ptr<const Concept> _pimpl;

public:
	template <class T>
	Mutation(std::shared_ptr<T> pimpl) noexcept
		: Mutation { std::unique_ptr<const Concept> { std::make_unique<Model<T>>(std::move(pimpl)) } }
	{
	}

	[[nodiscard]] static constexpr std::string_view getObjectType() noexcept
	{
		return { R"gql(Mutation)gql" };
	}
};

} // namespace graphql::taraxa::object

#endif // MUTATIONOBJECT_H
