#include "SyncStateObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::taraxa {
namespace object {

SyncState::SyncState(std::unique_ptr<const Concept>&& pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames SyncState::getTypeNames() const noexcept
{
	return {
		R"gql(SyncState)gql"sv
	};
}

service::ResolverMap SyncState::getResolvers() const noexcept
{
	return {
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(knownStates)gql"sv, [this](service::ResolverParams&& params) { return resolveKnownStates(std::move(params)); } },
		{ R"gql(currentBlock)gql"sv, [this](service::ResolverParams&& params) { return resolveCurrentBlock(std::move(params)); } },
		{ R"gql(highestBlock)gql"sv, [this](service::ResolverParams&& params) { return resolveHighestBlock(std::move(params)); } },
		{ R"gql(pulledStates)gql"sv, [this](service::ResolverParams&& params) { return resolvePulledStates(std::move(params)); } },
		{ R"gql(startingBlock)gql"sv, [this](service::ResolverParams&& params) { return resolveStartingBlock(std::move(params)); } }
	};
}

void SyncState::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void SyncState::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver SyncState::resolveStartingBlock(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getStartingBlock(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver SyncState::resolveCurrentBlock(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getCurrentBlock(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver SyncState::resolveHighestBlock(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getHighestBlock(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver SyncState::resolvePulledStates(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getPulledStates(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert<service::TypeModifier::Nullable>(std::move(result), std::move(params));
}

service::AwaitableResolver SyncState::resolveKnownStates(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getKnownStates(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert<service::TypeModifier::Nullable>(std::move(result), std::move(params));
}

service::AwaitableResolver SyncState::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(SyncState)gql" }, std::move(params));
}

} // namespace object

void AddSyncStateDetails(const std::shared_ptr<schema::ObjectType>& typeSyncState, const std::shared_ptr<schema::Schema>& schema)
{
	typeSyncState->AddFields({
		schema::Field::Make(R"gql(startingBlock)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv))),
		schema::Field::Make(R"gql(currentBlock)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv))),
		schema::Field::Make(R"gql(highestBlock)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv))),
		schema::Field::Make(R"gql(pulledStates)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Long)gql"sv)),
		schema::Field::Make(R"gql(knownStates)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Long)gql"sv))
	});
}

} // namespace graphql::taraxa
