#include "LogObject.h"
#include "AccountObject.h"
#include "TransactionObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::taraxa {
namespace object {

Log::Log(std::unique_ptr<const Concept>&& pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames Log::getTypeNames() const noexcept
{
	return {
		R"gql(Log)gql"sv
	};
}

service::ResolverMap Log::getResolvers() const noexcept
{
	return {
		{ R"gql(data)gql"sv, [this](service::ResolverParams&& params) { return resolveData(std::move(params)); } },
		{ R"gql(index)gql"sv, [this](service::ResolverParams&& params) { return resolveIndex(std::move(params)); } },
		{ R"gql(topics)gql"sv, [this](service::ResolverParams&& params) { return resolveTopics(std::move(params)); } },
		{ R"gql(account)gql"sv, [this](service::ResolverParams&& params) { return resolveAccount(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } },
		{ R"gql(transaction)gql"sv, [this](service::ResolverParams&& params) { return resolveTransaction(std::move(params)); } }
	};
}

void Log::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void Log::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver Log::resolveIndex(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getIndex(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<int>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver Log::resolveAccount(service::ResolverParams&& params) const
{
	auto argBlock = service::ModifiedArgument<response::Value>::require<service::TypeModifier::Nullable>("block", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getAccount(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)), std::move(argBlock));
	resolverLock.unlock();

	return service::ModifiedResult<Account>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver Log::resolveTopics(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getTopics(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert<service::TypeModifier::List>(std::move(result), std::move(params));
}

service::AwaitableResolver Log::resolveData(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getData(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<response::Value>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver Log::resolveTransaction(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getTransaction(service::FieldParams(service::SelectionSetParams{ params }, std::move(directives)));
	resolverLock.unlock();

	return service::ModifiedResult<Transaction>::convert(std::move(result), std::move(params));
}

service::AwaitableResolver Log::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(Log)gql" }, std::move(params));
}

} // namespace object

void AddLogDetails(const std::shared_ptr<schema::ObjectType>& typeLog, const std::shared_ptr<schema::Schema>& schema)
{
	typeLog->AddFields({
		schema::Field::Make(R"gql(index)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Int)gql"sv))),
		schema::Field::Make(R"gql(account)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Account)gql"sv)), {
			schema::InputValue::Make(R"gql(block)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Long)gql"sv), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(topics)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Bytes32)gql"sv))))),
		schema::Field::Make(R"gql(data)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Bytes)gql"sv))),
		schema::Field::Make(R"gql(transaction)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Transaction)gql"sv)))
	});
}

} // namespace graphql::taraxa
