void AddQueryDetails(const std::shared_ptr<schema::ObjectType>& typeQuery, const std::shared_ptr<schema::Schema>& schema)
{
	typeQuery->AddFields({
		schema::Field::Make(R"gql(block)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Block)gql"sv), {
			schema::InputValue::Make(R"gql(number)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Long)gql"sv), R"gql()gql"sv),
			schema::InputValue::Make(R"gql(hash)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Bytes32)gql"sv), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(blocks)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Block)gql"sv)))), {
			schema::InputValue::Make(R"gql(from)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Long)gql"sv)), R"gql()gql"sv),
			schema::InputValue::Make(R"gql(to)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Long)gql"sv), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(transaction)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(Transaction)gql"sv), {
			schema::InputValue::Make(R"gql(hash)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Bytes32)gql"sv)), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(gasPrice)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(BigInt)gql"sv))),
		schema::Field::Make(R"gql(chainID)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(BigInt)gql"sv))),
		schema::Field::Make(R"gql(dagBlock)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(DagBlock)gql"sv), {
			schema::InputValue::Make(R"gql(hash)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Bytes32)gql"sv), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(dagBlocks)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(DagBlock)gql"sv)))), {
			schema::InputValue::Make(R"gql(dagLevel)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Long)gql"sv), R"gql()gql"sv),
			schema::InputValue::Make(R"gql(count)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Int)gql"sv), R"gql()gql"sv),
			schema::InputValue::Make(R"gql(reverse)gql"sv, R"md()md"sv, schema->LookupType(R"gql(Boolean)gql"sv), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(nodeState)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(CurrentState)gql"sv))
	});
}

